sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var pawnArray = [];

    var gameCode = new GlideRecord('x_297381_fffun_now_mind_code');
    gameCode.addQuery('game', current.sys_id);
    gameCode.query();

    if (gameCode.next()) {
        pawnArray.push(gameCode.pawn_1);
        pawnArray.push(gameCode.pawn_2);
        pawnArray.push(gameCode.pawn_3);
        pawnArray.push(gameCode.pawn_4);
    }
	gs.info(pawnArray,"MDE");

    var newGuess = new GlideRecord('x_297381_fffun_now_mind_guess');
	newGuess.initialize();

	var rightPlace = 0;
	var rightColour = 0;

	var pawns = pawnArray.join();

    if (current.pawn_1 == pawnArray[0]) rightPlace += 1;
	else if(pawns.indexOf(current.pawn_1)>-1) rightColour += 1;

    if (current.pawn_2 == pawnArray[1]) rightPlace += 1;
	else if(pawns.indexOf(current.pawn_2)>-1) rightColour += 1;

    if (current.pawn_3 == pawnArray[2]) rightPlace += 1;
	else if(pawns.indexOf(current.pawn_3)>-1) rightColour += 1;

    if (current.pawn_4 == pawnArray[3]) rightPlace += 1;
	else if(pawns.indexOf(current.pawn_4)>-1) rightColour += 1;

	newGuess.right_place = rightPlace;
	newGuess.right_colour = rightColour;	
	newGuess.game = current.sys_id;
    newGuess.pawn_1 = current.pawn_1;
    newGuess.pawn_2 = current.pawn_2;
    newGuess.pawn_3 = current.pawn_3;
    newGuess.pawn_4 = current.pawn_4;
    newGuess.insert();

})(current, previous);
